import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import {Row, Col, ListGroup} from 'react-bootstrap'
import { listProducts } from '../actions/productActions'
import Loader from './Loader'
import Message from './Message'



const Category = () => {

    const dispatch = useDispatch()

    const categoryList = useSelector(state => state.productList)
    const { products, loading, error } = categoryList

    

    useEffect(() => {
        dispatch(listProducts())
    }, [dispatch])

    return loading ? <Loader/> : error ?  <Message variant='danger'>{error}</Message> : (
  
       
            <ListGroup>
                <Row>
                    <Link className="btn btn-outline-primary my-3" to='/'>
                                All
                            </Link>
             {products.map(product => (
                 
                 <Row key={product.category}>
                 <Col 
                 className = "d-flex justify-content-center" >

                
                         <Link className="btn btn-outline-primary my-3" to='/카메라'>
                             {product.category}
                         </Link>


                 </Col>
                 </Row>
             ))}
                </Row>
            </ListGroup>
   
    )
}

export default Category
