import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { PayPalButton } from 'react-paypal-button-v2'
import { Link } from 'react-router-dom'
import {Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { getOrderDetails } from '../actions/orderActions'

const OrderScreen = ({match}) => {
    const orderId = match.params.id

    const [sdkReady, setSdkReady] = useState(false)
    
    const dispatch = useDispatch()

  
    const orderDetails = useSelector(state => state.orderDetails)
    const { order, loading, error } = orderDetails 

    const orderPay = useSelector(state => state.orderPay )
    const { loading:loadingPay , success:successPay } = orderPay  

   
     if(!loading){
        // order.itemsPrice = order.orderItems.reduce((acc, item) => acc + order.item.price * order.item.qty, 0)
     }

     const successPaymentHandler = () =>{
         console.log("pay")
     }

    useEffect(() => {
        const addPayPalScript = async () => {
            const { data: clientId } = await axios.get('/api/config/paypal')
            const script = document.createElement('script')
            script.type = 'text/javascript'
            script.src = `https://www.paypal.com/sdk/js?client-id=${clientId}`
            script.async = true
            script.onload = () => {
                setSdkReady(true)
            }
            document.body.appendChild(script)
         }

    
    
        if(!order || successPay){
            dispatch(getOrderDetails(orderId))
        }else if(!order.isPaid){
            if(!window.paypal){
                addPayPalScript()
            }else {
                setSdkReady(true)
            }
        }

    }, [ dispatch ,orderId, order, successPay])



    return loading ? <Loader /> : error ? 
    <Message variant='danger'>{error}</Message> : 
    <> 
        <h1>주문번호 {order._id}</h1>
        <Row>
               <Col md={8}>
                   <ListGroup variant='flush'>
                       <ListGroup.Item>
                           <h2>배송지</h2>
                           <p>
                           <strong>이름 : </strong> {order.user.name}
                           </p>
                           <p>
                           <strong>이메일 : </strong> <a href={`mailto:${order.user.email}`}>{order.user.email}</a>
                           </p>
                           <p>
                               <strong>주소 : </strong>
                               {order.shippingAddress.address},
                               {order.shippingAddress.city},
                               {order.shippingAddress.postalCode},{' '}
                               {order.shippingAddress.country}
                           </p>
                           {order.isDelived ? (
                                <Message variant='success'>배달완료 {order.DelivedAt}</Message>
                            ) : (
                                <Message variant='danger'>배달준비</Message>
                            )}
                       </ListGroup.Item>

                       <ListGroup.Item>
                            <h2>결제 방법</h2>
                            <p>
                            <strong>지불 선택 :</strong>
                            {order.paymentMethod}
                            </p>
                            {order.isPaid ? (
                                <Message variant='success'>결제완료 {order.paidAt}</Message>
                            ) : (
                                <Message variant='danger'>미결제</Message>
                            )}
                            
                            
                       </ListGroup.Item>

                       <ListGroup.Item>
                            <h2>주문 상품</h2>
                            {order.orderItems.length === 0 ? 
                                <Message>주문이 없습니다</Message> : (
                                    <ListGroup varaiant='flush'>
                                        {order.orderItems.map((item, index) => (
                                            <ListGroup.Item key={index}>
                                                <Row>
                                                    <Col md={1}>
                                                        <Image 
                                                        src={item.image} 
                                                        alt={item.name}
                                                        fluid 
                                                        rounded
                                                        />
                                                    </Col>

                                                    <Col>
                                                        <Link to={`/product/${item.product}`}>
                                                            {item.name}
                                                        </Link>
                                                    </Col>
                                                    <Col md={4}>
                                                          {item.price}원 X {item.qty}개 = {item.qty * item.price}원
                                                    </Col>
                                                </Row>
                                            </ListGroup.Item>
                                        ))}
                                    </ListGroup>
                                )}
                       </ListGroup.Item>
                   </ListGroup>
               </Col>

               <Col md={4}>
                   <Card>
                       <ListGroup variant='flush'>
                           <ListGroup.Item>
                               <h2>주문 요약</h2>                     
                           </ListGroup.Item>

                           <ListGroup.Item>
                               <Row>
                                   <Col>주문 상품</Col>   
                                        <Col> 원</Col>                                                              
                               </Row>
                           </ListGroup.Item>
                            
                           <ListGroup.Item>
                                <Row>
                                    <Col>배송비</Col>
                                    <Col>{order.shippingPrice}원</Col>
                                </Row>                  
                           </ListGroup.Item>

                           <ListGroup.Item>
                               <Row>
                                   <Col>관세 및 부과세 </Col>
                                   <Col>{order.taxPrice}원</Col>
                               </Row>
                           </ListGroup.Item>

                           <ListGroup.Item>
                               <Row>
                                   <Col>주문 총액</Col>
                                   <Col>{order.totalPrice}원</Col>
                               </Row>
                           </ListGroup.Item>

                              {!order.isPaid && (
                                  <ListGroup.Item>
                                    {loadingPay && <Loader />}
                                    {!sdkReady ? (
                                        <Loader />
                                    ) : (
                                        <PayPalButton
                                            amount = {order.totalPrice}
                                            onSuccess = {successPaymentHandler}
                                        />
                                    )}
                                 </ListGroup.Item>   
                              )}                  
                       </ListGroup>
                   </Card>
               </Col>
           </Row>
    </>
}

export default OrderScreen
