import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import {Row, Col, ListGroup, label, InputGroup} from 'react-bootstrap'
import Loader from './Loader'
import Message from './Message'


import { listProducts } from '../actions/productActions'


const Category = ({history}) => {
    const [keyword, setKeyword] = useState('')

    const onSubmitHandler = (e) => {
        e.preventDefault()
        if(keyword === '/'){
            history.push('/')
        }else{

        }
    }

    const dispatch = useDispatch()

    const categoryList = useSelector(state => state.productList)
    const { products, loading, error } = categoryList

    
    useEffect(() => {
        dispatch(listProducts())
    }, [dispatch])

    return loading ? <Loader/> : error ?  <Message variant='danger'>{error}</Message> : (
  
       
            <ListGroup>
                <Row>
                    <Link className="btn btn-outline-primary my-3"to='/'>
                                All
                            </Link>
             {products.map(product => (
                 
                 <Row key={product._id}>
                 <Col className = "d-flex justify-content-center" >

                    <InputGroup type="checkbox" className="btn-check">
                        <label className="btn btn-outline-primary my-3" 
                                onSubmit={onSubmitHandler}
                                onchange= { setKeyword(product.category)}>
                             {product.category}
                         </label>
                    </InputGroup>
                        


                 </Col>
                 </Row>
             ))}
                </Row>
            </ListGroup>
   
    )
}

export default Category
